pragma solidity ^0.4.22;


import "./SafeMath.sol";

contract JobContract {
  using SafeMath for uint;

  enum JobStatus {
    active,
    completed,
    cancelled,
    inDispute
  }

  struct Job {
    uint id;
    address owner;
    bytes32 ref;
    string docHash;
    JobStatus status;
    uint initialJobpot;
    uint jobpot;
    uint appFees;
    uint numApps;
  }

  uint private index;
  uint internal balance;
  Job[] internal jobs;

  function createJob(bytes32 ref, string docHash, uint initialJobpotPrize, uint appFees) public payable returns(uint) {
    // require(initialJobpotPrize > 0 && appFees > 0 && msg.value >= initialJobpotPrize);

    uint id = index++;

    Job memory job = Job(id, msg.sender, ref, docHash, JobStatus.active, initialJobpotPrize, initialJobpotPrize, appFees, 0);

    jobs.push(job);

    return id;
  }

  function getJobsCount() public view returns(uint) {
    return index;
  }

  function getJob(uint id) public view returns(bytes32, string, int8, uint, uint, uint) {
    return ( jobs[id].ref, jobs[id].docHash, int8(jobs[id].status), jobs[id].initialJobpot, jobs[id].jobpot, jobs[id].appFees);
  }
}
